mapper.select:
select cart.*, g.goods_name as goodsName, g.goods_img as goodsImg, g.shop_price as price, g2.goods_img as logoImg, g2.goods_name as logoName from e_goods_cart cart left join sys_user u on cart.user_id=u.user_id left join e_goods g on cart.goods_sn=g.goods_sn left join e_goods g2 on cart.made_logo=g2.goods_sn
cart.user_id=#{userId}
order by cart.create_time desc
